SELECT user_id, tel, user_name, nick_name, real_name, gender, avatar, create_time, remark FROM user WHERE deleted = 0
AND create_time > #{startDate, jdbcType=TIMESTAMP}
AND create_time < #{endDate, jdbcType=TIMESTAMP}
AND (user_name LIKE CONCAT('%', #{keyword}, '%') OR tel LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY user_id DESC

