mapper.select:
SELECT eid, open_id, user_id, nickname, real_name, gender, union_id, app_id, mct_no, tel, country_code, avatar, session_key, create_time, latest_login_time, login_count, longitude, latitude, location_update_time, register_longitude, register_latitude, recommend_eid, bound_with_user, bound_with_user_time, invite_count, remark, qcid, recommend_type, register_remark FROM user_weixin_mp WHERE deleted = 0 AND mct_no = #{mctNo}
AND create_time > #{startDate, jdbcType=TIMESTAMP}
AND create_time < #{endDate, jdbcType=TIMESTAMP}
AND recommend_eid = #{recommendEid}
AND (nickname LIKE CONCAT('%', #{keyword}, '%') OR real_name LIKE CONCAT('%', #{keyword}, '%') OR tel LIKE CONCAT('%', #{keyword}, '%') OR remark LIKE CONCAT('%', #{keyword}, '%') OR register_remark LIKE CONCAT('%', #{keyword}, '%') OR eid = #{keyword})
ORDER BY ${orderColumn} ${ordering}
ORDER BY eid DESC

SELECT DATE_FORMAT(create_time,'%Y-%m-%d') `date`,
       count(open_id) `count`
  FROM user_weixin_mp
 WHERE deleted = 0
   AND mct_no = #{mctNo}

   AND create_time > #{startDate, jdbcType=TIMESTAMP}


   AND create_time < #{endDate, jdbcType=TIMESTAMP}

 GROUP BY `date`
 ORDER BY `date` ASC
