mapper.select:
SELECT object_id, url, end_point, bucket, mct_no, venue_id, file_name, file_path, `path`, path_param, uploaded, `size`, file_type, mime_type, upload_by, height, width, remark, create_time, update_time FROM inbyte_object_storage WHERE deleted = 0
AND create_time > #{start_date, jdbcType=TIMESTAMP}
AND create_time < #{end_date, jdbcType=TIMESTAMP}
AND (file_name LIKE CONCAT('%', #{keyword}, '%') OR remark LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY objectId DESC
